elseif Gauss_point_number==3
    Gauss_coefficient_reference_triangle=[1/6,1/6,1/6];
    Gauss_point_reference_triangle=[1/2,0;1/2,1/2;0,1/2];
elseif Gauss_point_number==16
    Gauss_coefficient_reference_triangle=[0.00210036524393259;0.0281503830709537;0.0187158153136431;0.0379971476464707;0.00210036524393259;0.0281503830709537;0.0187158153136431;0.0379971476464707;0.00393768560836699;0.0527752773517468;0.0350877052561587;0.0712356205087275;0.00393768560836699;0.0527752773517468;0.0350877052561587;0.0712356205087275];
    Gauss_point_reference_triangle=[0.930568155800000,0.0646110632109869;0.0694318442000000,0.865957092589013;0.669990521800000,0.307096311525094;0.330009478200000,0.623471844274906;0.930568155800000,0.00482078098901307;0.0694318442000000,0.0646110632109869;0.669990521800000,0.0229131666749057;0.330009478200000,0.0465186775250943;0.930568155800000,0.0465186775250943;0.0694318442000000,0.623471844274906;0.669990521800000,0.221103222498164;0.330009478200000,0.448887299301836;0.930568155800000,0.0229131666749057;0.0694318442000000,0.307096311525094;0.669990521800000,0.108906255701836;0.330009478200000,0.221103222498164];
